configfiles.configfile:
################################# load libraries and read file #########################
library(Cardinal) library(gridExtra) library(KernSmooth) library(lattice)
## Read MALDI Imaging dataset
#if $infile.ext == 'imzml' msidata = readMSIData('infile.imzML') #elif $infile.ext == 'analyze75' msidata = readMSIData('infile.hdr') #else load('infile.RData') #end if
###################################### file properties in numbers ######################
## Number of features (mz) maxfeatures = length(features(msidata)) ## Range mz minmz = round(min(mz(msidata)), digits=2) maxmz = round(max(mz(msidata)), digits=2) ## Number of spectra (pixels) pixelcount = length(pixels(msidata)) ## Range x coordinates minimumx = min(coord(msidata)[,1]) maximumx = max(coord(msidata)[,1]) ## Range y coordinates minimumy = min(coord(msidata)[,2]) maximumy = max(coord(msidata)[,2]) ## Range of intensities minint = round(min(spectra(msidata)[]), digits=2) maxint = round(max(spectra(msidata)[]), digits=2) medint = round(median(spectra(msidata)[]), digits=2) ## Number of intensities > 0 npeaks= sum(spectra(msidata)[]>0) ## Spectra multiplied with mz (potential number of peaks) numpeaks = ncol(spectra(msidata)[])*nrow(spectra(msidata)[]) ## Percentage of intensities > 0 percpeaks = round(npeaks/numpeaks*100, digits=2) ## Number of empty TICs TICs = colSums(spectra(msidata)[]) NumemptyTIC = sum(TICs == 0)
## Processing informations processinginfo = processingData(msidata) centroidedinfo = processinginfo@centroided # TRUE or FALSE
## if TRUE write processinginfo if no write FALSE
## normalization if (length(processinginfo@normalization) == 0) { normalizationinfo='FALSE' } else { normalizationinfo=processinginfo@normalization } ## smoothing if (length(processinginfo@smoothing) == 0) { smoothinginfo='FALSE' } else { smoothinginfo=processinginfo@smoothing } ## baseline if (length(processinginfo@baselineReduction) == 0) { baselinereductioninfo='FALSE' } else { baselinereductioninfo=processinginfo@baselineReduction } ## peak picking if (length(processinginfo@peakPicking) == 0) { peakpickinginfo='FALSE' } else { peakpickinginfo=processinginfo@peakPicking }
### Read tabular file with peptide masses for heatmap images:
input_list = read.delim("$massfile", header = FALSE, stringsAsFactors = FALSE) if (ncol(input_list) == 1) { input_list = cbind(input_list, input_list) }
### calculate how many input masses are valid: inputmasses = input_list[input_list[,1]>minmz & input_list[,1]<maxmz,]
inputmz = inputmasses[,1] inputnames = inputmasses[,2]
if (nrow(input_list) == 1) { countpixels = sum(spectra(msidata)[features(msidata, mz = inputmz), ] >0) }else { countpixels = rowSums(spectra(msidata)[features(msidata, mz=inputmz),] >0) }
percentpixels = round(countpixels/pixelcount*100, digits=1) valuesdataframe = cbind(inputmz, cbind(countpixels, percentpixels))
write.table(valuesdataframe, file="$pixel_count", quote = FALSE, row.names = TRUE, col.names=NA, sep = "\t")
colnames(input_list)[1:2] = c("mz", "name") properties = c("Number of mz features", "Range of mz values [Da]", "Number of pixels", "Range of x coordinates", "Range of y coordinates", "Range of intensities", "Median of intensities", "Intensities > 0", "Number of zero TICs", "Preprocessing", "Normalization", "Smoothing", "Baseline reduction", "Peak picking", "Centroided", paste0("# valid masses in \n", "$massfile.display_name"))
values = c(paste0(maxfeatures), paste0(minmz, " - ", maxmz), paste0(pixelcount), paste0(minimumx, " - ", maximumx), paste0(minimumy, " - ", maximumy), paste0(minint, " - ", maxint), paste0(medint), paste0(percpeaks, " %"), paste0(NumemptyTIC), paste0(" "), paste0(normalizationinfo), paste0(smoothinginfo), paste0(baselinereductioninfo), paste0(peakpickinginfo), paste0(centroidedinfo), paste0(length(inputmz), "/", length(input_list[,1])))
property_df = data.frame(properties, values)
######################################## PDF ############################################# ########################################################################################## ##########################################################################################
pdf("heatmaps.pdf", fonts = "Times", pointsize = 12) plot(0,type='n',axes=FALSE,ann=FALSE) #if not $filename: #set $filename = $infile.display_name #end if title(main=paste("Quality control of MSI data\n\n", "Filename:", "$filename"))
############################# I) numbers #################################### #############################################################################
grid.table(property_df, rows= NULL)
if (npeaks > 0) {
if (length(inputmz) != 0) {
for (mass in 1:length(inputmz)) {
print(image(msidata, mz=inputmz[mass], strip = strip.custom(bg="lightgrey", par.strip.text=list(col="black", cex=.9)), lattice=TRUE, ylim = c(maximumy+1,minimumy-1), plusminus = $plusminus_dalton, contrast.enhance = "$image_contrast", smooth.image = "$image_smoothing", main= paste0(mass, ") ", inputnames[mass], " (", round(inputmz[mass], digits = 2)," Â± ", $plusminus_dalton, " Da)"))) } } else {print("The input masses were invalid")}
dev.off()
}else{ print("inputfile has no intensities > 0") dev.off() }
