tool.command:
@QUOTE_FOO@ @EXT_FOO@ #import re
## Preprocessing mkdir in && ln -s '$in' 'in/${re.sub("[^\w\-_]", "_", $in.element_identifier)}.$gxy2omsext($in.ext)' && mkdir out &&
## Main program call
set -o pipefail && @EXECUTABLE@ -write_ctd ./ && python3 '$__tool_directory__/fill_ctd.py' '@EXECUTABLE@.ctd' '$args_json' '$hardcoded_json' && echo ARGS && cat '$args_json' && echo HARD && cat '$hardcoded_json' && @EXECUTABLE@ -ini @EXECUTABLE@.ctd -in 'in/${re.sub("[^\w\-_]", "_", $in.element_identifier)}.$gxy2omsext($in.ext)' -out ${' '.join(["'out/%s.%s'"%(re.sub('[^\w\-_]', '_', _.element_identifier), $gxy2omsext("featurexml")) for _ in $in if _])}
## Postprocessing ${' '.join(["&& mv -n 'in/%(id)s.%(gext)s' 'out/%(id)s'"%{"id": re.sub('[^\w\-_]', '_', _.element_identifier), "gext": $gxy2omsext("featurexml")} for _ in $out if _])} #if $adv_opts_cond.adv_opts_selector=='advanced' and $adv_opts_cond.ctd_out_FLAG && mv '@EXECUTABLE@.ctd' '$ctd_out' #end if
